/* Basscss Color Base */

/*

   COLOR VARIABLES

   - Cool
   - Warm
   - Gray Scale

*/

:root {

  /* Cool */

  --aqua:   #7fdbff;
  --blue:   #0074d9;
  --navy:   #001f3f;
  --teal:   #39cccc;
  --green:  #2ecc40;
  --olive:  #3d9970;
  --lime:   #01ff70;


  /* Warm */

  --yellow: #ffdc00;
  --orange: #ff851b;
  --red:    #ff4136;
  --fuchsia:#f012be;
  --purple: #b10dc9;
  --maroon: #85144b;


  /* Gray scale */

  --white:  #fff;
  --silver: #ddd;
  --gray:   #aaa;
  --black:  #111;

}

body {
  color: var(--black);
  background-color: white;
}

a {
  color: var(--link-color, var(--blue) );
  text-decoration: none;
}

  a:hover {
    text-decoration: underline;
  }

pre, code {
  background-color: var(--pre-background-color, var(--silver) );
  border-radius: var(--border-radius);
}

hr {
  border: 0;
  border-bottom-style: solid;
  border-bottom-width: var(--border-width);
  border-bottom-color: var(--border-color);
}

.button {
  color: white;
  background-color: var(--button-background-color, var(--blue) );
  border-radius: var(--border-radius);
  transition-duration: .05s;
  transition-timing-function: ease-out;
  transition-property: box-shadow, background-color;
}

.button:hover {
  box-shadow: inset 0 0 0 20rem var(--darken-1);
}

.button:focus {
  outline: none;
  border-color: var(--darken-2);
  box-shadow: 0 0 2px 1px var(--darken-3);
}

.button:active,
.button.is-active {
  box-shadow: inset 0 0 0 20rem var(--darken-2),
    inset 0 3px 4px 0 var(--darken-3),
    0 0 1px var(--darken-2);
}

.button:disabled,
.button.is-disabled {
  opacity: .5;
}

:root {
  --darken-1: rgba(0,0,0,.0625);
  --darken-2: rgba(0,0,0,.125);
  --darken-3: rgba(0,0,0,.25);
  --darken-4: rgba(0,0,0,.5);
  --border-color: var(--darken-2);
  --border-width: 1px;
  --border-radius: 3px;
}

